syntax = "proto2";

package gdmj;


message cs_chupai //MJ_CS_CHUPAI = 1001
{	
	required int32            card = 1; //出的牌
}

message cs_action //MJ_CS_ACTION = 1002
{	
	required int32            card = 1; //action的牌
	required int32           action = 2; //action 动作:吃、碰、胡、杠（明杠，加杠，暗杠）
}

message cs_changeonecard //MJ_CS_CHANGE_ONE_CARD = 1003
{	
	required int32           card1 = 1; //选择的牌
	required int32           card2 = 2; //换成功目标的牌
}






message sc_gamestart 		//MJ_SC_GAMESTART = 1501
{
	required int32            bankerchair = 1;
	repeated int32            dice = 2;			//2个骰子
	required int32			  leftcardcount = 3;	//牌库剩余数量
	//required int32			actionmask = 4;		//用户动作，对应庄家的操作，暗杆或者天胡

	required int32			  fangui = 4;			//翻鬼
	required int32			  mammon = 5;			//财神，即定鬼

	required int32			  ismingpai = 6;	//是否是明牌
}

message sc_fapai
{
	required int32			chairid = 1;

	repeated int32          cards = 2;			//自己的手牌，不包括摸到的牌
}

message sc_mopai //MJ_SC_MOPAI = 1503
{	
	required int32            chairid = 1;
	required int32            card = 2; //摸的牌
	required int32            leftnum = 3; //还剩下多少牌
	required int32			actionmask = 4;	//动作掩码
	required int32			tail = 5;	//1表示末尾摸牌，0表示正常摸牌
	repeated int32			  cangangcards = 6;
}

message sc_chupai //MJ_SC_CHUPAI = 1504
{	
	required int32            chairid = 1;
	required int32            card = 2; //出的牌
	repeated int32			  handcards = 3;
}

message sc_action //MJ_SC_ACTION = 1505,,操作提示
{	
	required int32            provideuser = 1;		//提供者
	required int32            card = 2;
	required int32           actionmask = 3;
	repeated int32			  cangangcards = 4;
}

message sc_gangscore //杠分记录
{	
	repeated int64            gangscore = 1;
}

message sc_notifyactionresult //SC_NOTIFYACTIONRESULT = 1506
{	
	required int32            operateuser = 1;	//操作用户
	required int32            provideuser = 2;	//供应用户
	required int32            card = 3;			//操作扑克
	required int32           actionmask = 4;	//动作掩码...这个只有一个作用就是你碰了之后，手牌里还有杠。
	required int32            gangkind = 5;		//如果是杠，需要说明杠的类型
	required int32            operatecode = 6;	//操作代码

	repeated int32			  handcards = 7;
}

message tingdata
{
	required int32 outcard = 1;				//出哪张能听
	repeated int32 hucard = 2;				//这张牌出完，能胡哪几张牌
	repeated int32 hucardremaining = 3;		//胡牌的剩余数，与上面对应
	repeated int32 hufan = 4;				//胡牌翻数，与上面对应
}

message sc_notifyting	//听牌提示
{
	repeated tingdata ting = 1;
}

message weaveitem //组合牌，即吃碰杠
{
	required int32 weavekind = 1;			//组合类型
	required int32 gangkind = 2;			//如果是杠，杠的类型
	required int32 centercard = 3;			//中心扑克
	required int32 provideuser = 4;			//供应用户
	repeated int32 carddata = 5;			//麻将数据
}

message playerend //游戏结束时玩家的数据
{
	required int64 gamescore = 1;	//总输赢
	required int64 huscore = 2;		//胡牌输赢
	required int64 mascore = 3;		//码输赢
	repeated int32 carddata = 4;	//扑克列表，注意要如果该玩家胡了，无论自摸还是抢杠胡，供应牌都不在这里面了
	repeated weaveitem weave = 5;	//组合扑克。这里要特别注意，胡了的时候，所有的玩家的暗杆要打开来。
}

message sc_gameend //MJ_SC_NOTIFYPLAYERYUEFANGRESULT = 1508 游戏结束时发的包
{
	required int64 cellscore = 1;	//基础分，就是底分
	required int32 winer = 2;		//胜利者。-1表示流局
	required int32 provideuser = 3;	//供应用户
	required int32 providecard = 4;	//供应扑克
	required int32 huright = 5;		//胡的牌型
	required int32 newbanker = 6;	//新庄家，用于连庄的显示
	repeated int32 madata = 7;		//马数据
	repeated int32 zhongmadata = 8;	//中马数据
	required int32 nomagic = 9;		//是否无鬼加倍

	repeated playerend player = 10;
}

message sc_changeonecard
{
	required int32 issuccess = 1;	//是否成功，1成功，0失败，失败只有一种可能，牌库没有这张
	required int32 useraction = 2;	//用户换完牌能触发什么杠或胡
	required int32 oldcard = 3;		//要换的牌
	required int32 changecard = 4;	//换到的牌是哪张
	repeated int32 cards = 5;		//目前的所有的手牌
	repeated int32 cangangcards = 6;	//如果有杠牌权限，可杠的牌
}

