syntax = "proto2";

package twmj;

message mj_cs_chupai //MJ_CS_CHUPAI = 1001
{	
	required int32            card = 1; //出的牌

	optional int32            timeout = 2; //玩家是否是超时自动出牌
}

message mj_cs_action //MJ_CS_ACTION = 1002
{	
	required int32            card = 1; //action的牌
	required int32            action = 2; //action 动作:吃、碰、胡、杠（明杠，加杠，暗杠） 目前不支持吃，所以暂时不支持。
}

message mj_cs_qucikover //MJ_CS_QUCIKOVER = 1003
{	
	required int32            flag = 1; 
}

message mj_sc_gamecfg //MJ_SC_GAMECFG = 1519
{	
	required int32            raisehandtimeout = 1;
}


message mj_sc_gamestart //MJ_SC_GAMESTART = 1501
{
	required int32            bankerchair = 1;
	repeated int32            dice = 2;	
	required int32            junum = 3;
	required int32            allnum = 4;	
	required int32            wallcardnum = 5;	
	required int32            dicetime = 6;
	required int32            waittime = 7; //默认出牌超时时间

	optional int32            actiontime = 8;//金币场权限动作时间
	optional int32            autotuoguan = 9;	//是否托管
	optional int32            curbasescore = 10; //金币场底分
			
	required int32            quandirect = 11; //默认出牌超时时间
	required int32            quancount = 12; //默认出牌超时时间
	required int32			  ismingpai = 13;	//是否是明牌
}

message mj_sc_fapai //MJ_SC_FAPAI = 1502
{	
	required uint64           userid = 1;
	required int32            chairid = 2;
	required int32            cardnum = 3;
	repeated int32            cards = 4;
	repeated int32            hua = 5;	
	
	optional int32            curmopai = 6;		
	required int32			  leftnum = 7;
}

message mj_sc_mopai //MJ_SC_MOPAI = 1503
{	
	required int32            chairid = 1;
	required int32            card = 2; //摸的牌

	required int32            leftnum = 3; //还剩下多少牌
}

message mj_sc_chupai //MJ_SC_CHUPAI = 1504
{	
	required int32            chairid = 1;
	required int32            card = 2; //出的牌
	repeated int32			  handcards = 3;
}

message mj_sc_action //MJ_SC_ACTION = 1505
{	
	required int32            be_actioned_chairid = 1;
	repeated int32            card = 2;		//如果有杠牌权限，这里面是可杠的牌
	required int32            actionmask = 3;	
	optional int32            actiontime = 4;
}

message gangscore //杠分记录
{	
	repeated int32            chairid = 1;
	repeated int64            playergangscore = 2;
}
message mj_sc_notifyactionresult //MJ_SC_NOTIFYACTIONRESULT = 1506
{	
	required int32            be_actioned_chairid = 1;
	required int32            action_chairid = 2;
	required int32            card = 3;
	required int32            actionmask = 4;	
	repeated int32            downcards = 5;

	repeated int32			  handcards = 6;

	optional int32            chupaitime = 7;	
	optional gangscore        gangscore = 8; 
	optional int32		      gangtype = 9; 
}

