//#define WIK_LEFT					0x01								//左吃类型
//#define WIK_CENTER				0x02								//中吃类型
//#define WIK_RIGHT					0x04								//右吃类型
//#define WIK_PENG					0x08								//碰牌类型
//#define WIK_GANG					0x10								//杠牌类型

message onehand //一手牌
{	
	required int32            actionmask = 1; //参考上面的定义
	required int32            card = 2;
	required int32            be_actioned_chairid = 3;
}

//flag的定义：
//#define RESULT_FLAG_HOST         0x00000001 
//#define RESULT_FLAG_FANGPAO      0x00000002
//#define RESULT_FLAG_JIEPAO       0x00000004
//#define RESULT_FLAG_ZIMO         0x00000008

message faninfo 
{
	required int32            id = 1;
	required int32            fan = 2;
	required string           name = 3;
}

message playresult //游戏结束时玩家的数据
{	
	required int32            chairid = 1;
	required uint64			  userid = 2;
	required bytes            name = 3;
	repeated onehand          down_hand = 4;
	repeated int32            handcards = 5;
	required uint32           flag = 6; 
	required int32            score = 7; 
	required int32            gangscore = 8; 	
	required int32            allscore = 9;
	required int32            hulevel = 10;

	optional int64            moneychange = 11;
	optional uint32           headid = 12;
	
	repeated faninfo          faninfo = 13;    //胡牌的玩家番的详细定义
}

message mj_sc_notifygameresult //MJ_SC_NOTIFYGAMERESULT = 1507 游戏结束时发的包
{		
	repeated playresult       playergameresult = 1;
	optional string 		  gamedate = 2;
	required int32            ismingpai = 3; //本局是明牌吗？
	required int32            mopaicount = 4; //本局总共摸牌了多少张
	required int32            cjid = 5; //本局打算完成的成就id，是看视频来完成成就的。
}

message mj_sc_notifyhuacard //MJ_SC_NOTIFYHUACARD = 1518
{
	required int32            chairid = 1;		
	repeated int32            hua = 2;	
	required int32            leftnum = 3; //还剩下多少牌
}


message mj_sc_notifyhulessfan //MJ_SC_NOTIFYHULESSFAN = 1519
{	
	required int32            leastfan = 1;		
	required int32            fan = 2;		
}

message mj_sc_huanpai //MJ_SC_HUANPAI = 1520
{	
	required int32            oldcard = 1;		
	required int32            newcard = 2;		
}

message mj_sc_directcount //MJ_SC_DIRECTCOUNT = 1521
{
	required int32            continuewinnum = 1;		
	required int32            quandirect = 2;
	required int32            quancount = 3;
}
			
message achi
{
	required int32            id = 1;		
	required int32            finish = 2;		
	required int32            currentnum = 3;		
}
			
message mj_sc_notifyachi //MJ_SC_NOTIFYACHI = 1522 通知成就完成的情况
{
	repeated achi             achis = 2;		
}

message mj_sc_notifytingpai //MJ_SC_NOTIFYTINGPAI = 1523
{	
	required int32            chairid = 1;	
	required int32            isting = 2;	
	required int32            fromclient = 3;	
}
